import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Clock, Download, Search, Filter, FileText } from 'lucide-react';
import axios from 'axios';

const Timeline = () => {
  const { sessionId } = useParams();
  const [timelineData, setTimelineData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedTopics, setSelectedTopics] = useState([]);
  const [filteredSegments, setFilteredSegments] = useState([]);

  const fetchTimelineData = async () => {
    try {
      const response = await axios.get(`/results/${sessionId}`);
      setTimelineData(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching timeline data:', error);
      setError('Failed to load timeline data');
      setLoading(false);
    }
  };

  const filterSegments = () => {
    if (!timelineData) return;

    let filtered = timelineData.timeline_segments;

    // Filter by search term
    if (searchTerm) {
      filtered = filtered.filter(segment =>
        segment.transcript.toLowerCase().includes(searchTerm.toLowerCase()) ||
        segment.summary.toLowerCase().includes(searchTerm.toLowerCase()) ||
        segment.key_topics.some(topic => 
          topic.toLowerCase().includes(searchTerm.toLowerCase())
        )
      );
    }

    // Filter by selected topics
    if (selectedTopics.length > 0) {
      filtered = filtered.filter(segment =>
        segment.key_topics.some(topic => selectedTopics.includes(topic))
      );
    }

    setFilteredSegments(filtered);
  };

  useEffect(() => {
    fetchTimelineData();
  }, [sessionId]);

  useEffect(() => {
    if (timelineData) {
      filterSegments();
    }
  }, [timelineData, searchTerm, selectedTopics]);

  const getAllTopics = () => {
    if (!timelineData) return [];

    const allTopics = new Set();
    timelineData.timeline_segments.forEach(segment => {
      segment.key_topics.forEach(topic => allTopics.add(topic));
    });

    return Array.from(allTopics);
  };

  const toggleTopic = (topic) => {
    setSelectedTopics(prev =>
      prev.includes(topic)
        ? prev.filter(t => t !== topic)
        : [...prev, topic]
    );
  };

  const exportToPDF = async () => {
    try {
      const response = await axios.post(`/export/pdf/${sessionId}`);
      const downloadUrl = response.data.download_url;
      window.open(downloadUrl, '_blank');
    } catch (error) {
      console.error('Error exporting PDF:', error);
      alert('Failed to export PDF');
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-96">
        <div className="animate-spin w-12 h-12 border-4 border-blue-200 border-t-blue-600 rounded-full"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <div className="text-red-600 text-lg font-medium">{error}</div>
        <button 
          onClick={() => window.location.href = '/'}
          className="mt-4 btn-primary text-white px-6 py-2 rounded-lg font-medium"
        >
          Go Back Home
        </button>
      </div>
    );
  }

  return (
    <div>
      {/* Header */}
      <div className="bg-white rounded-xl shadow-sm p-6 mb-8">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-2xl font-bold text-gray-900 mb-2">
              Video Timeline Analysis
            </h1>
            <p className="text-gray-600">
              {timelineData?.metadata.video_filename} • {timelineData?.metadata.total_segments} segments
            </p>
          </div>
          <button 
            onClick={exportToPDF}
            className="btn-primary text-white px-6 py-2 rounded-lg font-medium flex items-center"
          >
            <Download className="w-4 h-4 mr-2" />
            Export PDF
          </button>
        </div>
      </div>

      {/* Filters */}
      <div className="bg-white rounded-xl shadow-sm p-6 mb-8">
        <div className="flex flex-col lg:flex-row gap-4">
          {/* Search */}
          <div className="flex-1">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <input
                type="text"
                placeholder="Search transcripts, summaries, or topics..."
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </div>

          {/* Topic Filter */}
          <div className="lg:w-80">
            <div className="relative">
              <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <select
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                onChange={(e) => e.target.value && toggleTopic(e.target.value)}
                value=""
              >
                <option value="">Filter by topic...</option>
                {getAllTopics().map(topic => (
                  <option key={topic} value={topic}>{topic}</option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {/* Selected Topics */}
        {selectedTopics.length > 0 && (
          <div className="mt-4 flex flex-wrap gap-2">
            {selectedTopics.map(topic => (
              <span
                key={topic}
                className="inline-flex items-center px-3 py-1 rounded-full text-sm bg-blue-100 text-blue-800 cursor-pointer"
                onClick={() => toggleTopic(topic)}
              >
                {topic}
                <span className="ml-1 text-blue-600">×</span>
              </span>
            ))}
          </div>
        )}
      </div>

      {/* Timeline */}
      <div className="space-y-6">
        {filteredSegments.map((segment, index) => (
          <div key={segment.id} className="timeline-card bg-white rounded-xl shadow-sm p-6">
            <div className="flex flex-col lg:flex-row gap-6">
              {/* Screenshot */}
              <div className="lg:w-80 flex-shrink-0">
                <img
                  src={segment.screenshot_path}
                  alt={`Screenshot at ${segment.formatted_time_range}`}
                  className="w-full h-48 object-cover rounded-lg border border-gray-200"
                />
                <div className="mt-2 flex items-center justify-between text-sm text-gray-600">
                  <div className="flex items-center">
                    <Clock className="w-4 h-4 mr-1" />
                    {segment.formatted_time_range}
                  </div>
                  <div className="text-right">
                    Confidence: {Math.round(segment.confidence_score)}%
                  </div>
                </div>
              </div>

              {/* Content */}
              <div className="flex-1">
                <div className="mb-4">
                  <h3 className="text-lg font-semibold text-gray-900 mb-2">
                    Segment {index + 1}
                  </h3>
                  <p className="text-gray-700 leading-relaxed">
                    {segment.summary}
                  </p>
                </div>

                {/* Topics */}
                {segment.key_topics.length > 0 && (
                  <div className="mb-4">
                    <h4 className="text-sm font-medium text-gray-900 mb-2">Key Topics:</h4>
                    <div className="flex flex-wrap gap-2">
                      {segment.key_topics.map(topic => (
                        <span
                          key={topic}
                          className="inline-block px-2 py-1 bg-gray-100 text-gray-700 text-xs rounded-md"
                        >
                          {topic}
                        </span>
                      ))}
                    </div>
                  </div>
                )}

                {/* Transcript */}
                <div className="bg-gray-50 rounded-lg p-4">
                  <h4 className="text-sm font-medium text-gray-900 mb-2 flex items-center">
                    <FileText className="w-4 h-4 mr-1" />
                    Transcript
                  </h4>
                  <p className="text-sm text-gray-700 italic">
                    "{segment.transcript}"
                  </p>
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>

      {filteredSegments.length === 0 && (
        <div className="text-center py-12">
          <div className="text-gray-500 text-lg">
            No segments match your current filters.
          </div>
          <button
            onClick={() => {
              setSearchTerm('');
              setSelectedTopics([]);
            }}
            className="mt-4 text-blue-600 hover:text-blue-800 font-medium"
          >
            Clear all filters
          </button>
        </div>
      )}
    </div>
  );
};

export default Timeline;
